pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "../utils/LinkedListWithUniqueKeys.sol";
import "./interfaces/IConfig.sol";
import "../global/interfaces/IGlobalCore.sol";
import "../utils/Ownable.sol";

contract Config is Initializable, IConfig, Ownable {
    using LinkedListWithUniqueKeys for LinkedListWithUniqueKeys.Bytes32List;

    // ------------------ Storage ------------------
    bytes32 private constant _STORAGE_SLOT = bytes32(uint256(keccak256("fluence.deal.storage.v1.config")) - 1);

    struct ConfigStorage {
        uint256 creationBlock;
        CIDV1 appCID;
        // --- deal config ---
        IERC20 paymentToken;
        uint256 collateralPerWorker;
        uint256 minWorkers;
        uint256 targetWorkers;
        uint256 maxWorkersPerProvider;
        uint256 pricePerWorkerEpoch;
        CIDV1[] effectors;
        // --- access ---
        AccessType accessType;
        LinkedListWithUniqueKeys.Bytes32List accessList;
    }

    ConfigStorage private _storage;

    function _getConfigStorage() private pure returns (ConfigStorage storage s) {
        bytes32 storageSlot = _STORAGE_SLOT;
        assembly {
            s.slot := storageSlot
        }
    }

    // ------------------ Immutable ------------------
    IERC20 private immutable _fluenceToken;
    IGlobalCore private immutable _globalCore_;

    // ------------------ Constructor ------------------
    constructor(IGlobalCore globalCore_) {
        _globalCore_ = globalCore_;
        _fluenceToken = globalCore_.fluenceToken();
    }

    // ------------------ Initializer ------------------
    function __Config_init(
        IERC20 paymentToken_,
        uint256 collateralPerWorker_,
        uint256 minWorkers_,
        uint256 targetWorkers_,
        uint256 maxWorkersPerProvider_,
        uint256 pricePerWorkerEpoch_,
        CIDV1[] calldata effectors_,
        AccessType accessType_,
        address[] calldata accessList_,
        address owner_
    ) internal onlyInitializing {
        __Ownable_init(owner_);

        __Config_init_unchained(
            paymentToken_,
            collateralPerWorker_,
            minWorkers_,
            targetWorkers_,
            maxWorkersPerProvider_,
            pricePerWorkerEpoch_,
            effectors_,
            accessType_,
            accessList_
        );
    }

    function __Config_init_unchained(
        IERC20 paymentToken_,
        uint256 collateralPerWorker_,
        uint256 minWorkers_,
        uint256 targetWorkers_,
        uint256 maxWorkersPerProvider_,
        uint256 pricePerWorkerEpoch_,
        CIDV1[] calldata effectors_,
        AccessType accessType_,
        address[] calldata accessList_
    ) internal onlyInitializing {
        ConfigStorage storage configStorage = _getConfigStorage();

        configStorage.creationBlock = block.number;

        // --- init deal config ---
        configStorage.paymentToken = paymentToken_;
        configStorage.collateralPerWorker = collateralPerWorker_;
        configStorage.minWorkers = minWorkers_;
        configStorage.targetWorkers = targetWorkers_;
        configStorage.maxWorkersPerProvider = maxWorkersPerProvider_;
        configStorage.pricePerWorkerEpoch = pricePerWorkerEpoch_;

        for (uint256 i = 0; i < effectors_.length; i++) {
            configStorage.effectors.push(effectors_[i]);
        }

        configStorage.accessType = accessType_;

        for (uint256 i = 0; i < accessList_.length; i++) {
            configStorage.accessList.push(bytes32(bytes20(accessList_[i])));
        }
    }

    // ------------------ View Internal Functions ------------------
    function _globalCore() internal view returns (IGlobalCore) {
        return _globalCore_;
    }

    // ------------------ View Functions ---------------------
    function paymentToken() public view returns (IERC20) {
        return _getConfigStorage().paymentToken;
    }

    function fluenceToken() public view returns (IERC20) {
        return _fluenceToken;
    }

    function creationBlock() public view returns (uint256) {
        return _getConfigStorage().creationBlock;
    }

    function pricePerWorkerEpoch() public view returns (uint256) {
        return _getConfigStorage().pricePerWorkerEpoch;
    }

    function targetWorkers() public view returns (uint256) {
        return _getConfigStorage().targetWorkers;
    }

    function collateralPerWorker() public view returns (uint256) {
        return _getConfigStorage().collateralPerWorker;
    }

    function minWorkers() public view returns (uint256) {
        return _getConfigStorage().minWorkers;
    }

    function effectors() public view returns (CIDV1[] memory) {
        return _getConfigStorage().effectors;
    }

    function accessType() public view returns (AccessType) {
        return _getConfigStorage().accessType;
    }

    function isInAccessList(address addr) public view returns (bool) {
        return _getConfigStorage().accessList.has(bytes32(bytes20(addr)));
    }

    function getAccessList() public view returns (address[] memory) {
        bytes32[] memory result = _getConfigStorage().accessList.toArray();

        /*
        TODO: mv to assembly
        uint256 length = result.length;
        assembly ("memory-safe") {
            return(result, mul(length, 32))
        }*/

        address[] memory result2 = new address[](result.length);
        for (uint256 i = 0; i < result.length; i++) {
            result2[i] = address(bytes20(result[i]));
        }

        return result2;
    }

    function appCID() external view returns (CIDV1 memory) {
        return _getConfigStorage().appCID;
    }

    function maxWorkersPerProvider() public view returns (uint256) {
        return _getConfigStorage().maxWorkersPerProvider;
    }

    // ------------------ Mutable Functions ------------------
    function setAppCID(CIDV1 calldata appCID_) external onlyOwner {
        _getConfigStorage().appCID = appCID_;

        emit AppCIDChanged(appCID_);
    }

    function changeAccessType(AccessType accessType_) external onlyOwner {
        _getConfigStorage().accessType = accessType_;
    }

    function removeFromAccessList(address addr) external onlyOwner {
        _getConfigStorage().accessList.remove(bytes32(bytes20(addr)));
    }
}
","keccak256":"0x7c44ec533a11fe56165ad6574696afe704c34d01123c88e258f58504bc3a0f0c","license":"Apache-2.0"},"contracts/deal/Deal.sol":{"content":"// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.19;

import "./DealStorageUtils.sol";
import "./WorkerManager.sol";
import "./interfaces/IDeal.sol";
import "./interfaces/IConfig.sol";
import "../global/interfaces/IGlobalCore.sol";
import "@openzeppelin/contracts/utils/structs/BitMaps.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract Deal is WorkerManager, IDeal {
    using BitMaps for BitMaps.BitMap;
    using SafeERC20 for IERC20;
    using DealStorageUtils for DealStorageUtils.Balance;

    // ------------------ Constants ------------------
    uint256 private constant _MIN_EPOCH_FOR_BALANCE_AMOUNT = 2;
    uint256 private constant _EPOCH_FOR_ENDING = 2;

    // ------------------ Types ------------------
    struct ComputeUnitPaymentInfo {
        uint256 startedEpoch;
        uint256 gapsDelta;
    }

    // ------------------ Storage ------------------
    bytes32 private constant _STORAGE_SLOT = bytes32(uint256(keccak256("fluence.deal.storage.v1.deal")) - 1);

    struct DealStorage {
        uint totalBalance;
        uint lockedBalance;
        uint gapsEpochCount;
        uint256 maxPaidEpoch;
        uint256 lastCommitedEpoch;
        mapping(bytes32 => ComputeUnitPaymentInfo) cUnitPaymentInfo;
    }

    DealStorage private _storage;

    function _getDealStorage() private pure returns (DealStorage storage s) {
        bytes32 storageSlot = _STORAGE_SLOT;
        assembly {
            s.slot := storageSlot
        }
    }

    // ------------------ Constructor ---------------------
    constructor(IGlobalCore globalCore_) Config(globalCore_) {
        _disableInitializers();
    }

    // ------------------ Init ------------------
    function initialize(
        IERC20 paymentToken_,
        uint256 collateralPerWorker_,
        uint256 minWorkers_,
        uint256 targetWorkers_,
        uint256 maxWorkersPerProvider_,
        uint256 pricePerWorkerEpoch_,
        CIDV1[] calldata effectors_,
        IConfig.AccessType accessType_,
        address[] calldata accessList_,
        address owner_
    ) public initializer {
        __Config_init(
            paymentToken_,
            collateralPerWorker_,
            minWorkers_,
            targetWorkers_,
            maxWorkersPerProvider_,
            pricePerWorkerEpoch_,
            effectors_,
            accessType_,
            accessList_,
            owner_
        );
    }

    // ------------------ Privat Functions ------------------
    function _calculateMaxPaidEpoch(
        uint currentEpoch,
        uint totalBalance,
        uint pricePerWorkerEpoch_,
        uint workerCount
    ) private pure returns (uint256) {
        return currentEpoch + totalBalance / (pricePerWorkerEpoch_ * workerCount);
    }

    function _preCommitPeriod(
        Status currentStatus,
        Status newStatus,
        DealStorageUtils.Balance memory balance,
        uint currentEpoch,
        uint maxPaidEpoch,
        uint lastCommitedEpoch,
        uint currentWorkerCount,
        uint pricePerWorkerEpoch_
    ) private pure {
        if (currentStatus == newStatus && currentStatus == Status.ACTIVE) {
            uint amount = (currentEpoch - lastCommitedEpoch) * pricePerWorkerEpoch_ * currentWorkerCount;

            balance.setTotalBalance(balance.getTotalBalance() - amount);
            balance.setLockedBalance(balance.getLockedBalance() + amount);
        } else if (
            (currentStatus == newStatus && newStatus == IStatusController.Status.INACTIVE) ||
            (currentStatus == IStatusController.Status.INACTIVE && newStatus == IStatusController.Status.ACTIVE)
        ) {
            balance.setGapsEpochCount(balance.getGapsEpochCount() + (currentEpoch - lastCommitedEpoch));
        } else if (currentStatus == Status.ACTIVE && newStatus == Status.INACTIVE) {
            uint amount = (maxPaidEpoch - lastCommitedEpoch) * pricePerWorkerEpoch_ * currentWorkerCount;

            balance.setTotalBalance(balance.getTotalBalance() - amount);
            balance.setLockedBalance(balance.getLockedBalance() + amount);
            balance.setGapsEpochCount(balance.getGapsEpochCount() + (currentEpoch - lastCommitedEpoch));
        }
    }

    function _postCommitPeriod(DealStorageUtils.Balance memory balance, Status currentStatus, Status newStatus, uint currentEpoch) private {
        DealStorage storage dealStorage = _getDealStorage();

        balance.commitToStorage(dealStorage);
        dealStorage.lastCommitedEpoch = currentEpoch;

        if (currentStatus != newStatus) {
            _setStatus(newStatus);
        }
    }

    // ------------------ Public View Functions ------------------
    function getStatus() public view returns (IStatusController.Status) {
        IStatusController.Status currentStatus = getCommittedStatus();

        if (currentStatus == IStatusController.Status.ENDED || currentStatus == IStatusController.Status.INACTIVE) {
            return currentStatus;
        }

        uint currentEpoch = _globalCore().currentEpoch();
        uint maxPaidEpoch = _getDealStorage().maxPaidEpoch;

        if (currentEpoch > maxPaidEpoch) {
            return IStatusController.Status.INACTIVE;
        } else if (currentEpoch > maxPaidEpoch) {
            return IStatusController.Status.INACTIVE;
        } else {
            return IStatusController.Status.ACTIVE;
        }
    }

    function getFreeBalance() public view returns (uint256) {
        DealStorage storage dealStorage = _getDealStorage();

        uint currentEpoch = _globalCore().currentEpoch();
        if (currentEpoch > dealStorage.maxPaidEpoch) {
            return 0;
        }

        return (currentEpoch - dealStorage.lastCommitedEpoch) * pricePerWorkerEpoch() * getComputeUnitCount();
    }

    function getRewardAmount(bytes32 computeUnitId) public view returns (uint) {
        DealStorage storage dealStorage = _getDealStorage();
        ComputeUnitPaymentInfo storage computeUnitPaymentInfo = dealStorage.cUnitPaymentInfo[computeUnitId];

        IStatusController.Status currentStatus = getCommittedStatus();
        IStatusController.Status newStatus = getStatus();

        uint currentEpoch = _globalCore().currentEpoch();
        uint workerCount = getComputeUnitCount();
        uint pricePerWorkerEpoch_ = pricePerWorkerEpoch();

        DealStorageUtils.Balance memory balance = DealStorageUtils.initCache(dealStorage);
        _preCommitPeriod(
            currentStatus,
            newStatus,
            balance,
            currentEpoch,
            dealStorage.maxPaidEpoch,
            dealStorage.lastCommitedEpoch,
            workerCount,
            pricePerWorkerEpoch_
        );

        uint reward = (currentEpoch - computeUnitPaymentInfo.startedEpoch) -
            (balance.getGapsEpochCount() - computeUnitPaymentInfo.gapsDelta) *
            pricePerWorkerEpoch_;

        return reward;
    }

    // ------------------ Public Mutable Functions ------------------
    function deposit(uint256 amount) external onlyOwner {
        DealStorage storage dealStorage = _getDealStorage();

        IStatusController.Status currentStatus = getCommittedStatus();
        IStatusController.Status newStatus = getStatus();

        uint currentEpoch = _globalCore().currentEpoch();
        uint workerCount = getComputeUnitCount();
        uint pricePerWorkerEpoch_ = pricePerWorkerEpoch();

        DealStorageUtils.Balance memory balance = DealStorageUtils.initCache(dealStorage);
        _preCommitPeriod(
            currentStatus,
            newStatus,
            balance,
            currentEpoch,
            dealStorage.maxPaidEpoch,
            dealStorage.lastCommitedEpoch,
            workerCount,
            pricePerWorkerEpoch_
        );

        balance.setTotalBalance(balance.getTotalBalance() + amount);

        dealStorage.maxPaidEpoch = _calculateMaxPaidEpoch(currentEpoch, balance.getTotalBalance(), pricePerWorkerEpoch_, workerCount);

        _postCommitPeriod(balance, currentStatus, newStatus, currentEpoch);

        paymentToken().safeTransferFrom(msg.sender, address(this), amount);

        emit Deposited(amount);
    }

    function withdraw(uint256 amount) external onlyOwner {
        DealStorage storage dealStorage = _getDealStorage();

        IStatusController.Status currentStatus = getCommittedStatus();
        IStatusController.Status newStatus = getStatus(); //TODO

        uint currentEpoch = _globalCore().currentEpoch();
        uint workerCount = getComputeUnitCount();
        uint pricePerWorkerEpoch_ = pricePerWorkerEpoch();

        if (currentStatus == IStatusController.Status.ENDED) {
            require(currentEpoch > endedEpoch() + _EPOCH_FOR_ENDING, "Can't withdraw before 2 epochs after deal end");
            dealStorage.totalBalance -= amount;
        } else {
            DealStorageUtils.Balance memory balance = DealStorageUtils.initCache(dealStorage);
            _preCommitPeriod(
                currentStatus,
                newStatus,
                balance,
                currentEpoch,
                dealStorage.maxPaidEpoch,
                dealStorage.lastCommitedEpoch,
                workerCount,
                pricePerWorkerEpoch_
            );

            balance.setTotalBalance(balance.getTotalBalance() - amount);

            uint minBalance = _MIN_EPOCH_FOR_BALANCE_AMOUNT * pricePerWorkerEpoch_ * targetWorkers();
            require(balance.getTotalBalance() >= minBalance, "Free balance needs to cover minimum 2 epochs");

            dealStorage.maxPaidEpoch = _calculateMaxPaidEpoch(currentEpoch, balance.getTotalBalance(), pricePerWorkerEpoch_, workerCount);
            _postCommitPeriod(balance, currentStatus, newStatus, currentEpoch);
        }

        paymentToken().safeTransfer(msg.sender, amount);

        emit Withdrawn(amount);
    }

    function withdrawRewards(bytes32 computeUnitId) external {
        DealStorage storage dealStorage = _getDealStorage();
        ComputeUnitPaymentInfo storage computeUnitPaymentInfo = dealStorage.cUnitPaymentInfo[computeUnitId];

        IStatusController.Status currentStatus = getCommittedStatus();
        IStatusController.Status newStatus = getStatus(); //TODO

        uint currentEpoch = _globalCore().currentEpoch();
        uint workerCount = getComputeUnitCount();
        uint pricePerWorkerEpoch_ = pricePerWorkerEpoch();

        DealStorageUtils.Balance memory balance = DealStorageUtils.initCache(dealStorage);
        _preCommitPeriod(
            currentStatus,
            newStatus,
            balance,
            currentEpoch,
            dealStorage.maxPaidEpoch,
            dealStorage.lastCommitedEpoch,
            workerCount,
            pricePerWorkerEpoch_
        );

        uint globalGapsEpochCount = balance.getGapsEpochCount();

        uint reward = (currentEpoch - computeUnitPaymentInfo.startedEpoch) -
            (globalGapsEpochCount - computeUnitPaymentInfo.gapsDelta) *
            pricePerWorkerEpoch_;

        require(reward > 0, "No rewards");

        computeUnitPaymentInfo.startedEpoch = currentEpoch;
        computeUnitPaymentInfo.gapsDelta = globalGapsEpochCount;

        balance.setLockedBalance(balance.getLockedBalance() - reward);

        _postCommitPeriod(balance, currentStatus, newStatus, currentEpoch);

        paymentToken().safeTransfer(msg.sender, reward);

        emit RewardWithdrawn(computeUnitId, reward);
    }

    function createComputeUnit(address computeProvider, bytes32 peerId) public returns (bytes32) {
        DealStorage storage dealStorage = _getDealStorage();

        IStatusController.Status currentStatus = getCommittedStatus();
        IStatusController.Status newStatus = getStatus(); //TODO

        uint currentEpoch = _globalCore().currentEpoch();
        (bytes32 computeUnitId, uint newComputeUnitCount) = _createComputeUnit(currentEpoch, computeProvider, peerId);
        uint pricePerWorkerEpoch_ = pricePerWorkerEpoch();

        ComputeUnitPaymentInfo storage computeUnitPaymentInfo = dealStorage.cUnitPaymentInfo[computeUnitId];

        DealStorageUtils.Balance memory balance = DealStorageUtils.initCache(dealStorage);
        _preCommitPeriod(
            currentStatus,
            newStatus,
            balance,
            currentEpoch,
            dealStorage.maxPaidEpoch,
            dealStorage.lastCommitedEpoch,
            newComputeUnitCount - 1,
            pricePerWorkerEpoch_
        );

        if (currentStatus == Status.INACTIVE && newComputeUnitCount > minWorkers()) {
            _setStatus(Status.ACTIVE); //TODO: verify other rules
        }

        dealStorage.maxPaidEpoch = _calculateMaxPaidEpoch(
            currentEpoch,
            balance.getTotalBalance(),
            pricePerWorkerEpoch_,
            newComputeUnitCount
        );
        _postCommitPeriod(balance, currentStatus, newStatus, currentEpoch);

        computeUnitPaymentInfo.startedEpoch = currentEpoch;
        computeUnitPaymentInfo.gapsDelta = balance.getGapsEpochCount();

        return computeUnitId;
    }

    function removeComputeUnit(bytes32 computeUnitId) public {
        DealStorage storage dealStorage = _getDealStorage();

        IStatusController.Status currentStatus = getCommittedStatus();
        IStatusController.Status newStatus = getStatus(); //TODO

        uint currentEpoch = _globalCore().currentEpoch();
        uint newComputeUnitCount = _removeComputeUnit(computeUnitId, currentStatus == Status.ENDED ? endedEpoch() : currentEpoch);
        uint pricePerWorkerEpoch_ = pricePerWorkerEpoch();

        DealStorageUtils.Balance memory balance = DealStorageUtils.initCache(dealStorage);
        _preCommitPeriod(
            currentStatus,
            newStatus,
            balance,
            currentEpoch,
            dealStorage.maxPaidEpoch,
            dealStorage.lastCommitedEpoch,
            newComputeUnitCount + 1,
            pricePerWorkerEpoch_
        );

        if (currentStatus == IStatusController.Status.ACTIVE && newComputeUnitCount < minWorkers()) {
            _setStatus(IStatusController.Status.INACTIVE); //TODO: verify other rules
        }

        dealStorage.maxPaidEpoch = _calculateMaxPaidEpoch(
            currentEpoch,
            balance.getTotalBalance(),
            pricePerWorkerEpoch_,
            newComputeUnitCount
        );
        _postCommitPeriod(balance, currentStatus, newStatus, currentEpoch);
    }

    function stop() external onlyOwner {
        DealStorage storage dealStorage = _getDealStorage();

        IStatusController.Status currentStatus = getCommittedStatus();
        IStatusController.Status newStatus = getStatus(); //TODO

        DealStorageUtils.Balance memory balance = DealStorageUtils.initCache(dealStorage);
        uint currentEpoch = _globalCore().currentEpoch();
        _preCommitPeriod(
            currentStatus,
            newStatus,
            balance,
            currentEpoch,
            dealStorage.maxPaidEpoch,
            dealStorage.lastCommitedEpoch,
            getComputeUnitCount(),
            pricePerWorkerEpoch()
        );
        _postCommitPeriod(balance, currentStatus, newStatus, currentEpoch);

        dealStorage.maxPaidEpoch = currentEpoch;
        _setStatus(IStatusController.Status.ENDED);
    }
}
","keccak256":"0x4146e1472610b1d4642bc165113ec713ce544695fabeb38a3ca2f54bda768898","license":"Apache-2.0"},"contracts/deal/DealStorageUtils.sol":{"content":"// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.19;

import "./Deal.sol";

library DealStorageUtils {
    // ------------------ TYPES ------------------
    struct Balance {
        uint _initTotalBalance;
        uint _initLockedBalance;
        uint _initGapsEpochCount;
        uint _totalBalance;
        uint _lockedBalance;
        uint _gapsEpochCount;
    }

    // ------------------ VIEWS ------------------
    function getTotalBalance(Balance memory self) internal pure returns (uint) {
        return self._totalBalance;
    }

    function getLockedBalance(Balance memory self) internal pure returns (uint) {
        return self._lockedBalance;
    }

    function getGapsEpochCount(Balance memory self) internal pure returns (uint) {
        return self._gapsEpochCount;
    }

    // ------------------ MUTABLES ------------------
    function initCache(Deal.DealStorage storage dealStorage) internal view returns (Balance memory) {
        uint totalBalance = dealStorage.totalBalance;
        uint lockedBalance = dealStorage.lockedBalance;
        uint gapsEpochCount = dealStorage.gapsEpochCount;

        return
            Balance({
                _initTotalBalance: totalBalance,
                _initLockedBalance: lockedBalance,
                _initGapsEpochCount: gapsEpochCount,
                _totalBalance: totalBalance,
                _lockedBalance: lockedBalance,
                _gapsEpochCount: gapsEpochCount
            });
    }

    function setTotalBalance(Balance memory self, uint totalBalance) internal pure {
        self._totalBalance = totalBalance;
    }

    function setLockedBalance(Balance memory self, uint lockedBalance) internal pure {
        self._lockedBalance = lockedBalance;
    }

    function setGapsEpochCount(Balance memory self, uint gapsEpochCount) internal pure {
        self._gapsEpochCount = gapsEpochCount;
    }

    function commitToStorage(Balance memory self, Deal.DealStorage storage dealStorage) internal {
        if (self._initTotalBalance != self._totalBalance) {
            dealStorage.totalBalance = self._totalBalance;
        }

        if (self._initLockedBalance != self._lockedBalance) {
            dealStorage.lockedBalance = self._lockedBalance;
        }

        if (self._initGapsEpochCount != self._gapsEpochCount) {
            dealStorage.gapsEpochCount = self._gapsEpochCount;
        }
    }
}
","keccak256":"0x60f2852d9cd8f6c13eff3e74ae35165d63c504de98a8a517af7aaf7c499a6ee8","license":"Apache-2.0"},"contracts/deal/StatusController.sol":{"content":"// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./Config.sol";
import "../utils/LinkedListWithUniqueKeys.sol";
import "./interfaces/IStatusController.sol";
import "../global/interfaces/IGlobalCore.sol";

abstract contract StatusController is Config, IStatusController {
    // ------------------ Storage ------------------
    bytes32 private constant _STORAGE_SLOT = bytes32(uint256(keccak256("fluence.deal.storage.v1.statusController")) - 1);

    struct StatusControllerStorage {
        Status status;
        uint startedEpoch;
        uint endedEpoch;
    }

    StatusControllerStorage private _storage;

    function _getStatusControllerStorage() private pure returns (StatusControllerStorage storage s) {
        bytes32 storageSlot = _STORAGE_SLOT;
        assembly {
            s.slot := storageSlot
        }
    }

    // ------------------ Public Mutable Functions ---------------------
    function getCommittedStatus() public view returns (Status) {
        return _getStatusControllerStorage().status;
    }

    function startedEpoch() public view returns (uint) {
        return _getStatusControllerStorage().startedEpoch;
    }

    function endedEpoch() public view returns (uint) {
        return _getStatusControllerStorage().endedEpoch;
    }

    // ------------------ Internal Mutable Functions ------------------
    function _setStatus(Status status) internal {
        StatusControllerStorage storage statusControllerStorage = _getStatusControllerStorage();

        statusControllerStorage.status = status;

        if (status == Status.ACTIVE && statusControllerStorage.startedEpoch == 0) {
            statusControllerStorage.startedEpoch = _globalCore().currentEpoch();
        } else if (status == Status.ENDED && statusControllerStorage.endedEpoch == 0) {
            statusControllerStorage.endedEpoch = _globalCore().currentEpoch();
        }
    }
}
","keccak256":"0x62b048027a538873959cbf61229e3d888786f51c37cab7433a06b9308c558473","license":"Apache-2.0"},"contracts/deal/WorkerManager.sol":{"content":"// SPDX-License-Identifier: Apache-2.0

pragma solidity ^0.8.19;

import "./Config.sol";
import "./interfaces/IWorkerManager.sol";
import "./StatusController.sol";
import "../utils/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

abstract contract WorkerManager is Config, StatusController, IWorkerManager {
    using LinkedListWithUniqueKeys for LinkedListWithUniqueKeys.Bytes32List;
    using SafeERC20 for IERC20;

    // ------------------ Types ------------------
    struct ComputeProviderInfo {
        uint256 computeUnitCount;
        LinkedListWithUniqueKeys.Bytes32List computeUnitsIds;
    }

    // ------------------ Storage ------------------
    bytes32 private constant _STORAGE_SLOT = bytes32(uint256(keccak256("fluence.deal.storage.v1.workerManager")) - 1);

    struct WorkerManagerStorage {
        // global area
        uint256 computeUnitCount;
        mapping(address => ComputeProviderInfo) computeProviderInfo;
        // compute units area
        mapping(bytes32 => ComputeUnit) computeUnitById;
        LinkedListWithUniqueKeys.Bytes32List computeUnitsIdsList;
        mapping(bytes32 => uint256) collateralWithdrawEpochByComputeUnitId;
    }

    WorkerManagerStorage private _storage;

    function _getWorkerManagerStorage() private pure returns (WorkerManagerStorage storage s) {
        bytes32 storageSlot = _STORAGE_SLOT;

        assembly {
            s.slot := storageSlot
        }
    }

    // ------------------ Constants ------------------
    bytes32 private constant _COMPUTE_UNIT_ID_PREFIX = keccak256("fluence.computeUnit.");
    uint256 private constant _WITHDRAW_EPOCH_TIMEOUT = 1;

    // ------------------ Internal Mutable Functions ------------------
    function _createComputeUnit(
        uint createdEpoch,
        address computeProvider,
        bytes32 peerId
    ) internal returns (bytes32 id, uint computeUnitCount) {
        WorkerManagerStorage storage workerStorage = _getWorkerManagerStorage();

        // check target workers count
        uint256 globalComputeUnitCount = workerStorage.computeUnitCount;
        require(globalComputeUnitCount < targetWorkers(), "Target workers reached");

        // check peerId isn't exist
        id = keccak256(abi.encodePacked(_COMPUTE_UNIT_ID_PREFIX, computeProvider, peerId));
        require(workerStorage.computeUnitById[id].owner == address(0x00), "Id already used");

        // check max workers per compute provider
        uint256 computeUnitCountByCP = workerStorage.computeProviderInfo[computeProvider].computeUnitCount;
        require(computeUnitCountByCP < maxWorkersPerProvider(), "Max workers per compute provider reached");

        // increase computeUnit count
        workerStorage.computeProviderInfo[computeProvider].computeUnitCount = ++computeUnitCountByCP;
        workerStorage.computeUnitCount = ++globalComputeUnitCount;

        // get required collateral
        uint256 collateral = collateralPerWorker();

        // create ComputeUnit
        workerStorage.computeUnitById[id] = ComputeUnit({
            id: id,
            peerId: peerId,
            workerId: bytes32(0),
            owner: computeProvider,
            collateral: collateral,
            created: createdEpoch
        });

        // add ComputeUnit to list
        workerStorage.computeUnitsIdsList.push(id);

        emit ComputeUnitCreated(id, computeProvider);

        // transfer collateral
        fluenceToken().safeTransferFrom(msg.sender, address(this), collateral);

        return (id, globalComputeUnitCount);
    }

    function _removeComputeUnit(bytes32 computeUnitId, uint lastWorkedEpoch) public returns (uint computeUnitCount) {
        WorkerManagerStorage storage workerStorage = _getWorkerManagerStorage();

        // check owner
        address computeProvider = workerStorage.computeUnitById[computeUnitId].owner;
        require(computeProvider != address(0x00), "ComputeUnit not found");
        require(computeProvider == msg.sender || msg.sender == owner(), "Only provider or deal owner can remove worker");

        // change computeUnit count
        uint256 newComputeUnitCount = workerStorage.computeUnitCount;
        workerStorage.computeProviderInfo[computeProvider].computeUnitCount--;
        workerStorage.computeUnitCount = --newComputeUnitCount;

        workerStorage.collateralWithdrawEpochByComputeUnitId[computeUnitId] = lastWorkedEpoch + _WITHDRAW_EPOCH_TIMEOUT;

        // remove ComputeUnit
        delete workerStorage.computeUnitById[computeUnitId];
        workerStorage.computeUnitsIdsList.remove(computeUnitId);

        emit ComputeUnitRemoved(computeUnitId);

        return newComputeUnitCount;
    }

    // ------------------ Public View Functions ---------------------
    function getComputeUnit(bytes32 id) public view returns (ComputeUnit memory) {
        return _getWorkerManagerStorage().computeUnitById[id];
    }

    function getComputeUnitCount() public view returns (uint256) {
        return _getWorkerManagerStorage().computeUnitCount;
    }

    function getComputeUnits() public view returns (ComputeUnit[] memory) {
        WorkerManagerStorage storage workerStorage = _getWorkerManagerStorage();

        ComputeUnit[] memory computeUnits = new ComputeUnit[](workerStorage.computeUnitCount);

        uint256 index = 0;
        bytes32 computeUnitId = workerStorage.computeUnitsIdsList.first();
        while (computeUnitId != bytes32(0)) {
            computeUnits[index] = workerStorage.computeUnitById[computeUnitId];
            index++;

            computeUnitId = workerStorage.computeUnitsIdsList.next(computeUnitId);
        }

        return computeUnits;
    }

    function getUnlockCollateralEpoch(bytes32 computeUnitId) external view returns (uint256) {
        return _getWorkerManagerStorage().collateralWithdrawEpochByComputeUnitId[computeUnitId];
    }

    // ------------------ Public Mutable Functions ---------------------
    function setWorker(bytes32 computeUnitId, bytes32 workerId) external {
        WorkerManagerStorage storage workerStorage = _getWorkerManagerStorage();
        ComputeUnit storage computeUnit = workerStorage.computeUnitById[computeUnitId];

        require(workerId != bytes32(0), "WorkerId can't be empty");

        // check owner
        address computeProvider = computeUnit.owner;
        require(computeProvider != address(0x00), "ComputeUnit not found");
        require(computeProvider == msg.sender, "Only provider or deal owner can remove worker");

        computeUnit.workerId = workerId;

        emit WorkerIdUpdated(computeUnitId, workerId);
    }

    function withdrawCollateral(bytes32 computeUnitId) external {
        WorkerManagerStorage storage workerStorage = _getWorkerManagerStorage();

        require(
            workerStorage.collateralWithdrawEpochByComputeUnitId[computeUnitId] <= _globalCore().currentEpoch(),
            "Collateral not available"
        );

        // get collateral and compute provider
        uint256 amount = workerStorage.computeUnitById[computeUnitId].collateral;
        address computeProvider = workerStorage.computeUnitById[computeUnitId].owner;

        // reset collateral withdraw info
        workerStorage.collateralWithdrawEpochByComputeUnitId[computeUnitId] = 0;

        emit CollateralWithdrawn(computeProvider, amount);

        // transfer collateral
        fluenceToken().safeTransfer(computeProvider, amount);

        emit CollateralWithdrawn(computeProvider, amount);
    }
}
